(16, '\r\n<table class=\"table table-striped table-hover\" dir=\"ltr\" id=\"specialtable\" cellspacing=\"1\" cellpadding=\"1\" border=\"1\" align=\"center\">\r\n	<caption>\r\n	<p dir=\"rtl\" align=\"center\">۱۴</p>\r\n\r\n	<p dir=\"rtl\" align=\"center\">&nbsp;</p>\r\n\r\n	<p dir=\"rtl\" align=\"center\"><span style=\"font-size:16px;     font-family: aviny;\r\n            font-size: 30px;\r\n            font-weight: 100;\">برنامه کلینیک تخصصی و فوق تخصصصی بیمارستان تهران</span></p>\r\n	</caption>\r\n	<thead>\r\n		<tr class=\"generalsurgery\" style=\"height:50px;\">\r\n			<th dir=\"rtl\" scope=\"col\" style=\"text-align: center; \"><span style=\"font-size:16px;\">ساعت</span></th>\r\n			<th dir=\"rtl\" scope=\"col\" style=\"text-align: center;\"><span style=\"font-size:16px;\">روز</span></th>\r\n			<th dir=\"rtl\" scope=\"col\" style=\"text-align: center;\"><span style=\"font-size:16px;\">پزشک</span></th>\r\n			<th dir=\"rtl\" scope=\"col\" style=\"text-align: center; \"><span style=\"font-size:16px;\">تخصص</span></th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr class=\"generalsurgery\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۱-۱۳</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">چهارشنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر کوروش شمیمی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جراحی عمومی</td>\r\n		</tr>\r\n		<tr class=\"generalsurgery\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\">۱۱-۱۳</span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center; \">شنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center; \">جناب آقای دکتر شروین شیبانی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center; \">جراحی عمومی</td>\r\n		</tr>\r\n		<tr class=\"generalsurgery\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\">۱۱-۱۳</span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">دوشنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر ناصر رهبر</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جراحی عمومی</td>\r\n		</tr>\r\n		<tr class=\"plasticsurgery\" style=\"height:40px;\">\r\n			<td dir=\"rtl\" style=\"text-align: center;\"><span style=\"font-size: 18px;\">۱۳-۱۵</span></td>\r\n			<td style=\"text-align: center; \">سه شنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center; \">جناب آقای دکتر عزت اله حقگذار</td>\r\n			<td dir=\"rtl\" style=\"text-align: center; \">جراحی پلاستیک</td>\r\n		</tr>\r\n		<tr class=\"plasticsurgery\" style=\"height:40px;\">\r\n			<td dir=\"rtl\" style=\"text-align: center;\"><strong><span style=\"font-size:18px;\">۱۳-۱۵</span></strong></td>\r\n			<td style=\"text-align: center; \">‍ پنج شنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center; \">جناب آقای دکتر عزت اله حقگذار</td>\r\n			<td dir=\"rtl\" style=\"text-align: center; \">جراحی پلاستیک</td>\r\n		</tr>\r\n		<tr class=\"handsurgery\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><strong><span style=\"font-size:18px;\">۱۳-۱۵</span></strong></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">شنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر هومن شریعت زاده</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جراحی دست</td>\r\n		</tr>\r\n		<tr class=\"handsurgery\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۳-۱۵</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center; \">پنج شنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center; \">جناب آقای دکتر هومن شریعت زاده</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جراحی دست</td>\r\n		</tr>\r\n		<tr class=\"backsurgery\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۳-۱۵</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">یکشنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر حمید بهتاش</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جراحی ستون فقرات</td>\r\n		</tr>\r\n		<tr class=\"ortopedy\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۲ - ۱۳</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">دوشنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر امیر شقاقی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">ارتوپدی</td>\r\n		</tr>\r\n		<tr class=\"ortopedy\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۳-۱۴</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">یکشنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر مهرداد بحر آبادی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">ارتوپدی</td>\r\n		</tr>\r\n		<tr class=\"ortopedy\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۳-۱۴</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">سه شنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر مهرداد بحر آبادی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">ارتوپدی</td>\r\n		</tr>\r\n		<tr class=\"kneesurgery\">\r\n			<td style=\"text-align:center; \"><span style=\"font-size:18px;\"><strong>۱۴:۳۰-۱۷</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">&nbsp;شنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دكتر هومن یحیی زاده</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">\r\n			<p>جراحی زانو</p>\r\n			</td>\r\n		</tr>\r\n		<tr class=\"kneesurgery\">\r\n			<td style=\"text-align:center; \"><span style=\"font-size:18px;\"><strong>۱۳-۱۵</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">یکشنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دكتر رضا شفيع پور</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">\r\n			<p>جراحی زانو</p>\r\n			</td>\r\n		</tr>\r\n		<tr class=\"zananzayeman\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><strong><span style=\"font-size:18px;\">۹-۱۱</span></strong></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">سه شنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر بیژن شاهرخی</td>\r\n			<td style=\"text-align: center;\">زنان و زایمان</td>\r\n		</tr>\r\n		<tr class=\"zananzayeman\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۹-۱۱</strong></span></td>\r\n			<td style=\"text-align: center;\">دوشنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">سرکار خانم مژگان بیگ زاده</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">زنان و زایمان</td>\r\n		</tr>\r\n		<tr class=\"zananzayeman\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۹-۱۱</strong></span></td>\r\n			<td style=\"text-align: center;\">چهارشنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">سرکار خانم مژگان بیگ زاده</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">زنان و زایمان</td>\r\n		</tr>\r\n		<tr class=\"zananzayeman\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><strong><span style=\"font-size:18px;\">۱۰-۱۲</span></strong></td>\r\n			<td style=\"text-align: center;\">يكشنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">سرکار خانم دکتر رویا موحد</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">زنان و زایمان</td>\r\n		</tr>\r\n		<tr class=\"zananzayeman\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۵-۱۷</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">سه شنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">سرکار خانم دکتر فرحناز امینی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">زنان و زایمان</td>\r\n		</tr>\r\n		<tr class=\"zananzayeman\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۶ -۱۸</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">یکشنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">سرکار خانم دکتر حمیده شبگرد مقدم</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">زنان و زایمان</td>\r\n		</tr>\r\n		<tr class=\"zananzayeman\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۲-۱۴</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">دوشنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">سرکار خانم دکتر ویدا ناجی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">زنان و زایمان</td>\r\n		</tr>\r\n		<tr class=\"maghz\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۵-۱۷</strong></span></td>\r\n			<td style=\"text-align: center;\">یکشنبه و سه شنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر سهراب شهزادی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جراحی مغز و ستون فقرات</td>\r\n		</tr>\r\n		<tr class=\"maghz\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۵-۱۸</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">دوشنبه و چهارشنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر مهرداد پازوکی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جراحی مغز و ستون فقرات</td>\r\n		</tr>\r\n		<tr class=\"asab\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۱-۱۴</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">شنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر سرژیک آزاریانس</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">داخلی مغز و اعصاب</td>\r\n		</tr>\r\n		<tr class=\"asab\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۲-۱۴</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">یکشنبه و سه شنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">سرکار خانم دکتر شیوا امامی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">داخلی مغز و اعصاب</td>\r\n		</tr>\r\n		<tr class=\"asab\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۹-۱۱</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">چهارشنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">سرکار خانم دکتر شیوا امامی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">داخلی مغز و اعصاب</td>\r\n		</tr>\r\n		<tr class=\"majari\" style=\"height:40px;\">\r\n			<td dir=\"rtl\" style=\"text-align: center;\">عصر</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">شنبه تا چهارشنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر شهریار مشرفی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جراحی کلیه و مجاری ادرار</td>\r\n		</tr>\r\n		<tr class=\"dakheli\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><strong><span style=\"font-size:18px;\">۱۶-۱۷</span></strong></td>\r\n			<td style=\"text-align: center;\">یکشنبه و سه شنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر سعید صفر نژاد</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">داخلی</td>\r\n		</tr>\r\n		<tr class=\"dakheli\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۹ -۱۱</strong></span></td>\r\n			<td style=\"text-align: center;\">پنج شنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر همایون مهران</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">داخلی</td>\r\n		</tr>\r\n		<tr class=\"ghalb\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۱ - ۱۲</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">دوشنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر داریوش کمال هدایت</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">قلب و عروق</td>\r\n		</tr>\r\n		<tr class=\"ghalb\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><strong>۹ -۱۲</strong></td>\r\n			<td style=\"text-align: center;\">شنبه - دوشنبه - سه شنبه -پنج شنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر بهروز مشایخی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">قلب و عروق</td>\r\n		</tr>\r\n		<tr class=\"ghalb\" style=\"height:40px;\r\n                   \">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۹ - ۱۱</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">یکشنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر بهروز مشایخی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">قلب و عروق</td>\r\n		</tr>\r\n		<tr class=\"ghalb\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۰ - ۱۲:۳۰</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">چهارشنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر بهروز مشایخی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">قلب و عروق</td>\r\n		</tr>\r\n		<tr class=\"ghalb\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۴ - ۱۶</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">شنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">سرکار خانم دکتر سودابه سید زمانی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">قلب و عروق</td>\r\n		</tr>\r\n		<tr class=\"ghalb\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۴ - ۱۵</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">چهارشنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">سرکار خانم دکتر سودابه سید زمانی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">قلب و عروق</td>\r\n		</tr>\r\n		<tr class=\"ghalb\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۴ - ۱۸</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">یکشنبه تا سه شنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">سرکار خانم دکتر یاسمن قطبی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">قلب و عروق</td>\r\n		</tr>\r\n		<tr class=\"khoon\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۴ - ۱۵</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">دوشنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر عبدالعلی شهراسبی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">آنکولوژی خون</td>\r\n		</tr>\r\n		<tr class=\"khoon\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۵ - ۱۷</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">یکشنبه و سه شنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر کریم نصیرزاده</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">آنکولوژی خون</td>\r\n		</tr>\r\n		<tr>\r\n			<td class=\"ghodad\" style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۳ - ۱۵</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">شنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">سرکار خانم دکتر لاله قانعی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">غدد و دیابت</td>\r\n		</tr>\r\n		<tr class=\"nefrology\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۰ - ۱۲</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">دوشنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر فریدون پروشانی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">نفرولوژی</td>\r\n		</tr>\r\n		<tr class=\"nefrology\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۵ - ۱۷</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">يكشنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">سرکار خانم فرین رشید فرخی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">نفرولوژی</td>\r\n		</tr>\r\n		<tr class=\"govaresh\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۲ - ۱۳</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">دوشنبه - چهارشنبه - پنج شنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر هومن هوشنگی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">گوارش</td>\r\n		</tr>\r\n		<tr class=\"govaresh\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۶ - ۱۷</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">شنبه&nbsp;</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر جواد رستگار مقدم</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">گوارش</td>\r\n		</tr>\r\n		<tr class=\"govaresh\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۱ - ۱۲</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">سه شنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر جواد رستگار مقدم</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">گوارش</td>\r\n		</tr>\r\n		<tr class=\"govaresh\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\">۱۵ - ۱۶</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">چهارشنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر بيژن&nbsp; زرسازگار</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">گوارش</td>\r\n		</tr>\r\n		<tr class=\"ravan\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\">&nbsp;<strong>۱۲- ۱۴</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">دوشنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر کامران درخشان</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">اعصاب و روان</td>\r\n		</tr>\r\n		<tr class=\"rie\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۲ - ۱۴</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">چهار شنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر علیرضا نعمتی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">ریه</td>\r\n		</tr>\r\n		<tr class=\"halgh\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۳ - ۱۴</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">شنبه</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر فريد علي احیایی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">گوش و حلق وبینی</td>\r\n		</tr>\r\n		<tr class=\"halgh\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۳- ۱۴</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">یکشنبه&nbsp;</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر افشين قاسمی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">گوش و حلق وبینی</td>\r\n		</tr>\r\n		<tr class=\"halgh\" style=\"height:40px;\">\r\n			<td style=\"text-align: center;\"><span style=\"font-size:18px;\"><strong>۱۳ - ۱۴</strong></span></td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">&nbsp;سه شنبه&nbsp;</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">جناب آقای دکتر افشين قاسمی</td>\r\n			<td dir=\"rtl\" style=\"text-align: center;\">گوش و حلق وبینی</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:20px;\"><strong>داخلی کلینیک : ۲۲۱۹-۲۳۱۹</strong></span></p>\r\n', 20),
(17, '\r\n<p><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/323321.png\" style=\"margin: 0px auto ; display : block\"></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\"><span style=\"font-size:16px;\">به مناسبت گرامیداشت روز پرستار \" جشنواره من پرستارم \" را با شعار \"<span style=\"color:#B22222;\"> دست های مهربان تو، امید بخش زندگی</span> \" با هدف گسترش فرهنگ مهربانی، به بیمار در قالب آثاری همچون عکس، متن، فیلم، کلیپ، کاریکاتور و ... برگزار می نماید. علاقمندان جهت شرکت در جشنواره می توانند آثار خود را تا تاریخ ۹۸/۱۰/۱۰ به شماره ۰۹۱۹۰۸۶۹۸۵۶ واتس آپ نمایند به سه اثر برگزیده جوایزی در مراسم روز پرستار اهداء می شود. آثار ارسال شده در سایت بیمارستان تهران <a href=\"http://tehran-hospital.com/\"><span dir=\"LTR\">http://tehran-hospital.com/</span></a> و صفحه رسمی اینستاگرام <span dir=\"LTR\">Tehran_general_hospital</span> منتشر و قابل دسترس خواهد بود.</span></p>\r\n\r\n<p dir=\"RTL\" align=\"center\"><span style=\"font-size:16px;\"><span style=\"color:#A52A2A;\"><strong>همچنین نمایشگاهی از آثار راه یافته به جشنواره همزمان با روز جشن پرستار برپا می شود.</strong></span></span></p>\r\n\r\n<p dir=\"RTL\" align=\"center\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\" align=\"center\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/edsds2.png\" style=\"float: left;\"></p>\r\n\r\n<p dir=\"RTL\" align=\"center\">&nbsp;</p>\r\n', 21),
(18, '<p dir=\"rtl\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/qwwq1.png\" style=\"float: right;\">&nbsp; &nbsp;</p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px;\">&nbsp;</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px;\">&nbsp;<span style=\"color:#FF0000;\"><strong>مراسم معارفه آقای حميد كريمی</strong></span> به عنوان مدير جديد بيمارستان تهران روز يكشنبه مورخ ۹۸/۰۸/۱۲&nbsp;ساعت ۹:۳۰ در سالن&nbsp;اجتماعات&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;دكتر&nbsp;پارسا با حضور مديرعامل، اعضای هيئت مديره، مديران، پزشكان و&nbsp;پرسنل برگزار گرديد.</span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\"><span style=\"font-size:18px;\"><strong><span style=\"color:#FF0000;\">به گزارش روابط عمومی بيمارستان تهران؛</span> </strong>در ابتدای مراسم دكتر شميمی مديرعامل محترم بيمارستان به سياق هميشگی در سخنانی ضمن تقدير از خدمات دكتر كاكوئی و تاكيد بر بستری سازی مناسب انجام شده ابراز اميدواری نمودند. ايشان در ادامه ضمن معرفی آقای كريمی به عنوان مدير جديد سازمان،&nbsp; برای وی آرزوی موفقيت كردند.</span></p>\r\n\r\n<p dir=\"RTL\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/wq222.png\" style=\"margin: 0px auto ; display : block\"></p>\r\n\r\n<p dir=\"RTL\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/4rfsw3.png\" style=\"margin: 0px auto ; display : block\"></p>', 22),
(19, '<p><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/qasw341.png\" style=\"margin: 0px auto ; display : block\"></p>\r\n\r\n<p dir=\"RTL\" align=\"center\"><span style=\"color:#FF0000;\"><span style=\"font-size:18px;\"><strong>برگزاری مانور اطفاء حريق و نجات بيمارستان تهران</strong></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\"><span style=\"font-size:18px;\">با توجه به لزوم آمادگی تمامی پرسنل بيمارستان تهران در زمينه عملكرد مناسب در زمان وقوع بحران و بلايا، نياز به برگزاری تمرين های متعدد با موضوعات متنوع در زمينه بحران و بلايا برای پرسنل می باشد به همين منظور هماهنگی های لازم در جهت برگزاری مانور اطفاء حريق و نجات با ايستگاه ۱۸ آتش نشانی صورت گرفت.</span></p>\r\n\r\n<p dir=\"RTL\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/2sgs2.png\" style=\"margin: 0px auto ; display : block\"></p>\r\n\r\n<p dir=\"RTL\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\"><span style=\"font-size:18px;\"><span style=\"color:#FF0000;\"><b>به گزارش روابط عمومی بيمارستان تهران؛</b></span> اين مانور با همكاری و مشاركت ايستگاه ۱۸ آتش نشانی و با شبيه سازی شرايط بحرانی و سناريوی از پيش تعيين شده از طبقه يك ساختمان شماره</span>&nbsp;<span style=\"font-size:18px;\">۲</span> <span style=\"font-size:18px;\">بيمارستان تهران به علت اتصالی كابل برق در واحد دبيرخانه آغاز گرديد.</span></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/3dww3.png\" style=\"float: left;\"><span style=\"font-size:18px;\">&nbsp;بنا به اين گزارش، عملياتی چون قطع برق، غيرفعالسازي آسانسورها، خروج سريع كاركنان از راه پله ها، انتقال&nbsp; مصدومان فرضی به اورژانس و ... در اين مانور انجام شد. بر اساس برنامه ريزی دقيق صورت گرفته مانور با مشاركت&nbsp; و همكاری نيروی اطفاء حريق و امداد و نجات، ماموران نيروی انتظامی، اورژانس، تكنسين ها و پرسنل بيمارستان تهران&nbsp; صبح جمعه ۱۹ مهر ماه ۹۸ در ساختمان شماره ۲ بيمارستان تهران واقع در خيابان كريمخان، خيابان سنايی، ميدان سنايی&nbsp; برگزار گرديد.&nbsp;</span></p>\r\n\r\n<p dir=\"RTL\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/sqwr4.png\" style=\"margin: 0px auto ; display : block\"></p>\r\n\r\n<p dir=\"RTL\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/ahda5.png\" style=\"margin: 0px auto ; display : block\"></p>\r\n\r\n<p dir=\"RTL\"><span style=\"font-size:18px;\"><span style=\"color:#FF0000;\"><b>هدف اصلی در اين مانور</b>؛</span> افزايش مهارت و آگاهی پرسنل در برابر حوادث احتمالی غير مترقبه و انجام بهترين اقدامات از زمان وقوع حريق تا رسيدن نيروهای آتش نشانی همچنين ايجاد هماهنگی نيروهای امداد و نجات و امنيت درون سازمانی و برون سازمانی و سنجش نقاط ضعف و قوت&nbsp;در راستای عملياتی كردن مديريت بحران در بيمارستان تهران بود.</span></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/fbsa6.png\" style=\"float: right;\"><span style=\"font-size: 18px;\">&nbsp; پس از&nbsp;پايان مانوردر روز دوشنبه&nbsp;۲۲ مهرماه جلسه بررسی مانور با حضور رياست&nbsp;ايستگاه ۱۸ آتش نشانی&nbsp; &nbsp;دربيمارستان تهران برگزار گرديد عملكرد واحدها&nbsp;مورد بررسی قرار گرفت و نواقص مشاهده شده به منظور انجام&nbsp; &nbsp;اقدامات اصلاحی بيان گرديد.</span></p>\r\n\r\n<p dir=\"RTL\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/adweww7.png\" style=\"margin: 0px auto ; display : block\"></p>\r\n\r\n<p dir=\"RTL\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/bfss8.png\"></p>\r\n\r\n<p dir=\"RTL\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\">&nbsp;</p>', 23),
(20, '<p><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/13ddq1.png\" style=\"margin: 0px auto ; display : block\"></p>\r\n\r\n<p dir=\"rtl\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style=\"font-size:20px;\">&nbsp;<strong>جابجائی&nbsp;در مسئوليت ها امری&nbsp;عادی&nbsp;است و نشانه پويائی&nbsp;است.</strong></span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px;\">پالايش نيروی&nbsp;انسانی&nbsp;و ظرفيت سازی&nbsp;موجب رشد و تعالی&nbsp;سازمان ها است و به كارگيری&nbsp;نيروی&nbsp;انسانی&nbsp;كه دارای&nbsp;انگيزه محكم و بالائی&nbsp;هستند قطعاً موجب رشد و توسعه در جهت اهداف سازمانی&nbsp;خواهد شد.</span></p>\r\n\r\n<p dir=\"rtl\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\"><span style=\"font-size:18px;\"><span style=\"color:#B22222;\"><strong>به گزارش روابط عمومی&nbsp;بيمارستان تهران؛<img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/askjhgf2.png\" style=\"float: left;\"></strong></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\"><span style=\"font-size:18px;\">مراسم توديع جناب آقای&nbsp;دكتر جواد كاكوئی&nbsp;مديريت محترم بيمارستان تهران در روز دوشنبه مورخ ۹۸/۷/۲۲ ساعت ۱۰:۳۰ با</span></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\"><span style=\"font-size:18px;\">حضور جناب آقای&nbsp;دكتر شروين شيبانی&nbsp;عضو محترم هيئت مديره، مديران و مسئولين واحد ها در اتاق جلسات برگزار شد.</span></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\"><span style=\"font-size:18px;\">در ابتدای&nbsp;اين مراسم آقای&nbsp;دكتر شيبانی&nbsp;ضمن اشاره به بعضی&nbsp;از ويژگی&nbsp;های&nbsp;مديريتی&nbsp;آقای&nbsp;دكتر كاكوئی&nbsp;در طی&nbsp;مدت مسئوليت </span></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\"><span style=\"font-size:18px;\">ايشان در يك سال گذشته برای&nbsp;ايشان آرزوی&nbsp;موفقيت نمودند.</span></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\"><span style=\"font-size:18px;\">آقای&nbsp;دكتر كاكوئی&nbsp;نيز طی&nbsp;سخنانی&nbsp;ضمن تشكر از اعتماد مديرعامل محترم در طی&nbsp;مدت گذشته از همكاری&nbsp;تمام كاركنان</span></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\"><span style=\"font-size:18px;\">قدردانی&nbsp;نمودند.</span></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\"><span style=\"font-size:18px;\">پس از پذيرايی&nbsp;جلسه در ساعت ۱۱:۳۰ خاتمه يافت</span>.</p>\r\n\r\n<p dir=\"RTL\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/1dasfg3.png\" style=\"margin: 0px auto ; display : block\"></p>\r\n\r\n<p dir=\"RTL\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/bdxg4.png\" style=\"margin: 0px auto ; display : block\"></p>\r\n\r\n<p dir=\"RTL\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/adssw8.png\" style=\"margin: 0px auto ; display : block\"></p>\r\n\r\n<p dir=\"RTL\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/adsads9.png\"></p>', 24),
(21, '<p dir=\"rtl\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/dfdfdd6.png\" style=\"float: left;\"></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\"><span style=\"font-size:20px;\">اهتمام و فعاليت ما همواره بر اين اصل استوار بوده است كه خدمتي متنوع و ارزنده با روش های&nbsp;نوين در حوزه سلامت با برخورداری از توان علمي، همت و تلاش متخصصين و در سايه تعامل پزشكان، پرستاران و نيروهای جوان، پرانگيزه و توانمند به بيماران و مراجعه كنندگان بيمارستان تهران ارائه نمائيم و قطعاً تعهد به نوآوری و تضمين كيفيت بالای خدمات با نظم بيشتر ضامن حفظ رابطه بلند مدت با بيماران و خانواده های گرامی آن ها و تداوم اين ارتباط می گردد.</span></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\"><span style=\"font-size:20px;\">اين نكته مهم را در نظر داشته باشيد كه پزشكی هنری دشوار است و نياز جدی به مشاركت گروهی دارد، لذا پزشكان بايد به يك زبان مشترك رسيده تا بتوانند از لحاظ دانش و تجربه در كنار يكديگر و در قالب يك تيم به حل مشكلات بيماران بپردازند.</span></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\"><span style=\"font-size:20px;\">هر يك از پزشكان تمايل به ارائه خدمات پزشكی به بهترين نحو ممكن براي بيمارانشان را دارند، از اين رو ديدن شادی بيمار پس از حصول سلامتی رضايت خاطر را براي پزشك به ارمغان خواهد آورد كه اين امر خود عامل اصلی برای ايجاد انگيزه بيشتر در ارائه خدمات حرفه ای پزشك می باشد. سعی و تلاش&nbsp; ما بر اين است تا بتوانيم در مسير تحقق اهداف فوق&nbsp; همراه شما عزيزان در جهت افزايش سطح انتظارات، ميزان آسايش و رفاه بيماران و بر طرف سازی نيازهای مرتبط با دارو و درمان، گامی شايسته در محيطی آرام برداريم.</span></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\"><span style=\"font-size:20px;\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/sdssdsd7.png\" style=\"float: right; width: 409px; height: 311px;\"></span></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\"><span style=\"font-size:20px;\">&nbsp;</span></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\"><span style=\"font-size:20px;\">اميد است اين ديدگاه ميزان رضايتمندی بي نظير شما عزيزان از عملكرد ما در جهت ارتقا سلامت، فراهم سازی خدمات بازتوانی و پيشبرد اهداف اين مجموعه را در پی داشته باشد.</span></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\"><span style=\"font-size:20px;\">همواره نظرات ارزشمند شما باعث پيشرفت و بهبود خدمات رسانی بهتر و افزودن خدمات جديد ما بوده است از اين رو مشتاقانه منتظر ارسال ايده ها، پيشنهادات و انتقادات شما عزيزان هستيم.</span></p>\r\n\r\n<p dir=\"RTL\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\r\n\r\n<p dir=\"RTL\" align=\"center\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/xvaa18.png\"></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\">&nbsp;</p>', 26),
(22, '<p><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/policy.jpg\"></p>', 25),
(23, '\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align: right;\">&nbsp;</p>\r\n\r\n<p style=\"text-align: right;\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/dfds21.png\" style=\"margin: 0px auto; display: block; width: 100%;\"></p>\r\n\r\n<p><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/weews2.png\" style=\"margin: 0px auto; display: block; width: 100%;\"></p>\r\n\r\n<p><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/aewe3.png\" style=\"margin: 0px auto; display: block; width: 100%;\"></p>\r\n\r\n<p><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/dfewf4.png\" style=\"margin: 0px auto; display: block; width: 100%;\"></p>\r\n\r\n<p><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/sdsd6.png\" style=\"margin: 0px auto; display: block; width: 100%;\"></p>\r\n', 32),
(24, '<p dir=\"rtl\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/dfddf7.png\" style=\"float: left;\"><br>\r\n<span style=\"color:#0000CD;\"><span style=\"font-size:22px;\"><strong>بيمارستان تخصصی&nbsp;تهران؛ </strong></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align: justify;\"><span style=\"font-size:20px;\">&nbsp;با نيم قرن تجربه اندوزی توانسته است در دوره های مختلف پاسخگوی نيازهای بيماران باشد اين مركز درمانی با تخصص های مختلف با هدف ارائه خدمات درمانی به شهروندان تهرانی و هموطنان گرامی با تكيه بر توانمندی ها، دانش تخصصی و تجربه موجود توانسته است جايگاه خود را به عنوان يك مركز درمانی قابل اعتماد در كشورهای همسايه نيز نهادينه سازد. برآنيم كه با برنامه ريزی هدفمند و آينده نگرانه نقش پررنگ تری را در جهت ارائه خدمات با كيفيت به بيماران و همراهان با هدف جلب رضايتمندی آن ها ايفا نمائيم.</span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align: justify;\"><span style=\"font-size:20px;\">فعاليت های بيمارستان در زمينه جراحی ( جراحی عمومی، زنان و زايمان، گوش و حلق و بينی <span dir=\"LTR\">ENT</span>، ارتوپدی، پلاستيك، جراحی مغز و اعصاب، كليه و مجاری ادرار ) و تخصص های داخلی (قلب و عروق، خون و انكولوژی، ريه، گوارش، مغز و اعصاب، غدد، كليه و فشارخون،&nbsp; روانپزشكی، عفونی و تب ) می باشد. اعمال جراحی كم تهاجمی پيشرفته، حضور اساتيد فرهيخته و وجود دستگاه های مجهز و پيشرفته در اتاق عمل، واحد تصويربرداری، آزمايشگاه، وجود بخش های مراقبت ويژه&nbsp; &nbsp;&nbsp;(<span dir=\"LTR\"> ICU</span> جراحی، <span dir=\"LTR\">ICU</span> داخلی و<span dir=\"LTR\">CCU </span>) و اورژانس از ديگر امتيازات اين بيمارستان است. واحدهای پاراكلينيك بيمارستان شامل آزمايشگاه، پاتوبيولوژی، فيزيوتراپی، تصويربرداری، داروخانه و درمانگاه می باشد. بیمارستان همچنين شامل واحدهای اداری، مالی و پشتيبانی است.</span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align: justify;\"><span style=\"font-size:20px;\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/bdsa8.png\" style=\"float: left;\"></span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align: justify;\"><span style=\"font-size:20px;\">ما همواره بر اين باور هستيم كه بتوانيم با تمركز بر شايسته سالاری خدمات ارزنده و ماندگاری به هموطنان عزيز و ساير بيمارانی كه از خارج كشور مراجعه می نمايند در مسير پيشرفت و ارتقا خدمات بهداشتی درمانی ارائه نمائيم.</span></p>', 31),
(25, '<p dir=\"rtl\" style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/sdsfhs1.png\" style=\"margin : 0px auto ; display : block\"></p>\r\n\r\n<p dir=\"rtl\" style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\"><span style=\"font-size:16px;\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/ddfaaa2.png\" style=\"width: 162px; height: 204px; float: right;\"></span></p>\r\n\r\n<p dir=\"rtl\" style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\"><span style=\"font-size:20px;\">بيمارستان تهران در سال۱۳۴۶ در خيابان كريم‌خان‌زند، خيابان مديری&nbsp;(‌شهيد حسينی فعلی) ‌با بخش‌های زنان و زايمان،‌ داخلی، ‌اطفال و جراحی با ظرفيت ۶۰ تخت خواب توسط چند تن از اساتيد دانشكده پزشكی تهران افتتاح شد و جزء اولين بيمارستان‌های خصوصی بود كه در آن زمان بنا گرديد.</span></p>\r\n\r\n<p dir=\"rtl\" style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\"><br>\r\n<span style=\"font-size:20px;\">نام بعضی از متخصصان و اساتيد خوشنام جامعه پزشكی كه پايه گذار و افتتاح كننده اين بيمارستان‌ بوده اند به شرح زير ‌می باشد.</span></p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\"><span style=\"font-size:20px;\"><strong>&nbsp;&nbsp;آقاي دكتر حسين پارسا (‌استاد جراحی زنان و زايمان‌)</strong></span></li>\r\n	<li style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\"><span style=\"font-size:20px;\"><strong>&nbsp;&nbsp;آقاي دكتر ناصر معين‌زاده (‌استاد گوش و حلق و بينی)</strong></span></li>\r\n	<li style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\"><span style=\"font-size:20px;\"><strong>&nbsp;&nbsp;آقاي دكتر پرويز طالقاني (‌استاد جراحي زنان وزايمان‌)</strong></span></li>\r\n	<li style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\"><span style=\"font-size:20px;\"><strong>&nbsp;&nbsp;آقاي دكتر عبدالله عليخاني (‌استاد و متخصص بيهوشي‌)</strong></span></li>\r\n	<li style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\"><span style=\"font-size:20px;\"><strong>&nbsp;&nbsp;آقاي دكتر همايون مهران (‌متخصص داخلي و قلب‌)</strong></span></li>\r\n</ul>\r\n\r\n<p dir=\"rtl\" style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\"><span style=\"font-size:16px;\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/ssssg3.png\" style=\"float: right; width: 266px; height: 193px;\"></span><span style=\"font-size:20px;\">در زمان جنگ تحميلی يك بخش كامل با تمام امكانات، پزشكان و پرسنل به طور رايگان برای خدمت به مجروحين عزيز جنگ اختصاص داده شد تا به طور شبانه‌روز‌ی به مداوا و ارائه خدمات به اين گروه از بيماران بپردازد.</span></p>\r\n\r\n<p dir=\"rtl\" style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\"><span style=\"font-size:20px;\">در طول سال‌ها به تدريج با گسترش تخصص‌های مختلف و بازسازي بخش‌ها و خريداری سيستم‌هاي جديد بيمارستان تبديل به بيمارستان تخصصی و فوق تخصصی ۱۰۰ تختخوابی شد.<br>\r\nبا احداث ساختمان جدید و برقراری ارتباط با ساختمان قدیمی ، تعدادی از واحدها به ساختمان مورد نظر انتقال داده شده و با توجه به افزایش فضای قابل بهره برداری در قسمتهای&nbsp; بستری و پاراکلینیک امکان ارتقاء ارائه خدمات بالینی به بیماران فراهم گردیده است .</span></p>\r\n\r\n<p dir=\"rtl\" style=\"font-family: &quot;B Nazanin&quot;;\"><span style=\"font-size:16px;\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/afew4.png\" style=\"margin: 0px auto ; display: block;\"></span></p>\r\n\r\n<p dir=\"rtl\" style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\"><span style=\"font-size:20px;\">&nbsp;اگر بخواهيم نگاهی اجمالی بر عملكرد بيمارستان تهران داشته باشيم بايد گفت‌، بيمارستان تهران با بهره‌مندی از پزشکان مجرب و پرستاران دلسوز و نیز تکیه بر اصل بیمارمداری توانسته در ارائه خدمات فوق‌تخصصی به بيماران پيشگام باشد.</span></p>\r\n\r\n<p dir=\"rtl\" style=\"font-family: &quot;B Nazanin&quot;;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"font-family: &quot;B Nazanin&quot;;\"><span style=\"font-size:16px;\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/vsergs5.png\" style=\"margin: 0px auto; display: block; width: 739px; height: 578px;\"></span></p>\r\n\r\n<p dir=\"rtl\" style=\"font-family: &quot;B Nazanin&quot;;\">&nbsp;</p>\r\n\r\n<p style=\"font-family: &quot;B Nazanin&quot;;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"font-family: &quot;B Nazanin&quot;; text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"font-family: B Nazanin;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"font-family: B Nazanin;\">&nbsp;</p>', 30),
(26, '<p dir=\"rtl\"><span style=\"font-size:18px;\"><strong><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/sdddwd6.png\" style=\"width: 100%;\"></strong></span><span style=\"font-size:22px;\"><strong>رسـالت</strong></span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align: justify;\"><span style=\"font-size:20px;\">بيمارستان تخصصی و فوق‌تخصصی تهران رسالت ارائه خدمات درمانی، تشخيصی و توانبخشی به كليه مراجعين بيمارستان را به عهده دارد‌، به نحوی كه بهترين نتيجه درمانی و بالاترين رضايت مراجعين تأمين شود و اين مهم با بهره‌مندی از همكاران مجرب و پرسنل دلسوز در ارائه خدمات به موقع و با كيفيت بالا با احترام به حقوق‌گيرندگان خدمت ميسر می گردد.</span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:22px;\"><strong>چشم انداز‌ (‌Vision‌)</strong></span></p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:20px;\">&nbsp;ارتقاء كمی و كيفی بيمارستان و دستيابی به رضايت كامل بيماران و ايجاد جايگاه ويژه در سطح استان تهران و ‌دريافت گواهينامه‌های عالی&nbsp;ارزشيابی و ساير گواهینامه‌های بين‌المللی.</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:20px;\">&nbsp;&nbsp;دارا بودن بالاترين سطح رضايتمندی بيماران و كاركنان.</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:20px;\">&nbsp;&nbsp;پيشتاز استفاده از امكانات نوين تشخيصی – درمانی و بكارگيری فن‌آوری های روز</span><span style=\"font-size:16px;\">.</span></li>\r\n</ul>\r\n\r\n<p dir=\"rtl\" style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:22px;\"><strong>ارزش‌ها&nbsp;(‌Values‌)</strong></span></p>\r\n\r\n<ul>\r\n	<li dir=\"rtl\" style=\"text-align: justify;\"><span style=\"font-size:16px;\"><strong>&nbsp;&nbsp;</strong></span><span style=\"font-size:20px;\"><strong>احترام به شأن و شخصيت و حقوق بيماران‌:&nbsp;</strong>&nbsp;بيماران ما مهمترين موجودی خود يعنی سلامتشان را در اختيار ما قرار داده‌اند‌، پس با آنها شرافتمندانه رفتار می كنيم.</span></li>\r\n	<li dir=\"rtl\" style=\"text-align: justify;\"><span style=\"font-size:20px;\">&nbsp;<strong>ارائه خدمات يكسان به همه افراد: </strong>همه افراد جامعه از هر قشر و با هر مليت (ايرانی و غيرايرانی) برای ما با ارزش هستند و حفظ سلامتی، حقوق انسانی و آرامش آنها را وظيفه يكايك خود می دانيم.</span></li>\r\n	<li dir=\"rtl\" style=\"text-align: justify;\"><span style=\"font-size:20px;\">&nbsp;&nbsp;<strong>تعامل با بيماران: </strong>ما باور داريم كه بيماران شركای اصلی و همراه ما در اعتلای خدماتمان هستند و نظرات آنان آينه‌ای از نقاط ضعف و&nbsp; قدرت ماست.</span></li>\r\n	<li dir=\"rtl\" style=\"text-align: justify;\"><span style=\"font-size:20px;\">&nbsp;&nbsp;<strong>همكاری بين‌بخشی:</strong> بيمارستان تأسيس شده است و ما اينجا هستيم زيرا مشتری ارزشمندی چون بيمار وجود دارد و از آنجا كه هدف همه ما خدمت دادن به اين مشتری ارزشمند است‌، با همدلی و همكاری بر لزوم وجود كار گروهی آگاهيم و همگی خود را حلقه زنجيری برای حفظ سلامت حقوق بيمار می دانيم.</span></li>\r\n	<li dir=\"rtl\" style=\"text-align: justify;\"><span style=\"font-size:20px;\">&nbsp;&nbsp;<strong>تعهد به ارائه خدمات نوين و استاندارد پزشكی:</strong> ما باور داريم كه ايده‌های جديد و به موقع و استفاده از اطلاعات به روز‌، به ارائه خدمات بهتر می انجامد‌، پس بر‌آنيم تا&nbsp;با استفاده از گايد‌لاين‌های معتبر‌، خدمات تشخيصی- درمانی و توانبخشی خود را بر اساس جديدترين توصيه‌های علمی رفرانس‌های معتبر به بيماران ارائه دهيم.</span></li>\r\n	<li dir=\"rtl\" style=\"text-align: justify;\"><span style=\"font-size:20px;\">&nbsp;&nbsp;<strong>حفظ احترام همراهان:</strong> ما بر اين باوريم كه همراهان بيمار از خود بيمار رنجورتر هستند پس حفظ آرامش و رفع نگرانی از آنان از وظايف ماست.</span></li>\r\n	<li dir=\"rtl\" style=\"text-align: justify;\"><span style=\"font-size:20px;\">&nbsp;&nbsp;<strong>آگاهی از نيازهای اجتماع:</strong> ما با تعامل با دانشگاه‌های علوم پزشكی كشور و وزارت بهداشت و درمان و آموزش پزشكی از نيازهای جامعه با خبر بوده و در موارد بحران آماده خدمت‌رسانی به عموم هم وطنانمان هستيم.</span></li>\r\n</ul>\r\n\r\n<p dir=\"rtl\" style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align: justify;\">&nbsp;</p>', 29);
INSERT INTO `display_description` (`id`, `description`, `display_id`) VALUES
(27, '<p dir=\"rtl\" style=\"text-align: justify;\"><span style=\"font-size:20px;\">بيمارستان تهران كه تحت پوشش دانشگاه علوم پزشكی ایران است در راستای مديريت ارائه خدمات درمانی در چارچوب دستورالعمل اعتباربخشی و حاكميت ‌بالينی به منظور ارتقای كيفيت خدمات درمانی، تشخيصی و توانبخشی اهداف ذيل را دنبال می كند:</span></p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:20px;\">&nbsp;&nbsp;ارتقاء مستمر كيفيت خدمات درمانی، تشخيصی و توانبخشی بيمارستان.</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:20px;\">&nbsp;&nbsp;ارتقاء مستمر كميت خدمات درمانی، تشخيصی و توانبخشی بيمارستان.</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:20px;\">&nbsp;&nbsp;اجرای استانداردهای اعتباربخشی.</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:20px;\">&nbsp;&nbsp;استقرار هفت محور حاکمیت بالینی.</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:20px;\">&nbsp;&nbsp;تأمين و افزايش سطح دسترسی مراجعين به خدمات درمانی مناسب.</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:20px;\">&nbsp;&nbsp;ارتقای مستمر توانمندی رؤسا، مديران و كاركنان و استفاده بهينه از منابع موجود.</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:20px;\">&nbsp;&nbsp;افزايش سطح رضايت بيماران.</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:20px;\">&nbsp;&nbsp;افزايش سطح رضايت پرسنل.</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:20px;\">&nbsp;&nbsp;ارتقاء مستمر سيستم آمار و اطلاعات و پايش و ارزشيابي كليه خدمات.</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:20px;\">&nbsp;&nbsp;اجرای فعاليت‌هايی برای حفظ و سالم‌سازی محيط زيست.</span></li>\r\n</ul>\r\n\r\n<p dir=\"rtl\" style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align: justify;\"><span style=\"font-size:20px;\">لذا به منظور دستيابی به اهداف فوق در اولين جلسه كميته مديريت راهبردی بيمارستان سياستهای مركز بشرح زير تعيين گرديد تا با استعانت از خداوند متعال و با همكاری كليه پزشكان و پرسنل به رسالت&nbsp; بيمارستان شامل ارائه خدمات درمانی، تشخيصی و توانبخشی به كليه مراجعين با حصول بهترين نتيجه درمانی و بالاترين ميزان رضايتمندی مراجعين و با بهره‌مندی از همكاران مجرب و پرسنل دلسوز با ارائه خدمات به موقع ، با كيفيت بالا و با احترام به حقوق‌گيرندگان خدمت نائل شويم.</span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align: justify;\"><br>\r\n<span style=\"font-size:20px;\">اينجانب با عزمی راسخ و اراده‌ای استوار و با تكيه بر همكاران گرانقدر‌، مديران‌، رؤسا، پزشكان‌، گروه پرستاری و ساير كاركنان توانمند‌، منابع لازم را جهت استقرار سيستم مديريت كيفيت در راستای&nbsp;حاكميت بالينی و اعتبار بخشی بيمارستان فراهم و به طور مستمر سيستم را بازنگری نموده و درك و اجرای كامل اين دو دستورالعمل را از كليه همكاران خواهانم‌.</span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align: justify;\"><br>\r\n<span style=\"font-size:20px;\">دكتر كوروش شميمی&nbsp;&nbsp;&nbsp;&nbsp;<br>\r\nمدير عامل بيمارستان تهران</span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"text-align: justify;\">&nbsp;</p>', 28),
(28, '<p>&nbsp;</p>\r\n\r\n<p dir=\"rtl\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/swfs2.png\" style=\"margin: 0px auto ; display : block\"></p>\r\n\r\n<p><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/hda4.png\" style=\"width: 100%;\"></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\"><span style=\"font-size:20px;\"><strong>مراجعه كننده گرامی از حضور و حسن انتخاب شما در جمع خانواده بزرگ بيمارستان تهران مفتخريم. بدون ترديد بيمارستان تهران از گذشته تا كنون با هدف تامين رضايت مراجعين محترم و خانواده های ارجمند آنان در راستای تحقق اين مهم بوده است.</strong></span></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\"><strong><span style=\"font-size:20px;\">اميد است كه لايق محبت بيكران شما عزيزان باشيم</span><span style=\"font-size:18px;\">.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></strong></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\"><strong><span style=\"font-size:18px;\"><img alt=\"\" class=\"img-responsive\" src=\"http://localhost/asset/image/vdsca5.png\" style=\"float: left;\"></span></strong></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\" style=\"text-align: justify;\">&nbsp;</p>', 27),
(29, '<p><img alt=\"\" src=\"http://localhost/asset/image/health-tourism.jpg\" style=\"width: 100%;\"></p>', 33);
